// Helper para sanitizar IDs de Pokemon para uso como chaves do Firebase
// Firebase não permite ".", "#", "$", "/", "[", ou "]" em chaves
const sanitizeFirebaseKey = (key) => {
  if (!key) return key
  return String(key).replace(/[.#$/\[\]]/g, '_')
}

