    const timestamp = new Date().toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' })
    const activePokemon = selectedTeamPokemon || selectedMasterNpcPokemon

    // Usar nome mascarado se o Pokémon NPC não estiver revelado
